Agent.Script:
#!/bin/bash ############ {0} ############ MESSAGE=$(echo -e "$MESSAGE") case "$IMPORTANCE" in 'normal' ) PRIORITY="3" ;; 'warning' ) PRIORITY="5" ;; 'alert' ) PRIORITY="7" ;; esac
# Remove any trailing slash SERVER_URL=${SERVER_URL%/}
curl -s -k -X POST \ -F "title=$TITLE" \ -F "message=$MESSAGE" \ -F "priority=$PRIORITY" \ ${SERVER_URL}/message?token=$APP_TOKEN 2>&1
